{"query":"mutation($username: String!, $password: String!) {
	login (
    data: {
    	username: $username,
    	password: $password 
    }
	) {
    __typename
    ... on AuthenticatedUser {
      name
      surname
      username
      role
      token
    }
    ... on ApiError {
      code
      message
      description
    }
	}
}","variables":{"username":"ddialar","password":"sorbetedelimon"}}

{"query":"mutation {
	logout {
    ... on SimpleResponse {
      result
    }
    ... on ApiError {
      code
      message
      description
    }
	}
}"}

{"query":"query ($id: String!) {
	user: getUser (id: $id) {
    __typename
    ... on User {
      id
      socialCareNumber
      idCard
      name
      surname
      birthDate
      genre
      username
      role
      token
      lastLoginAt
      createdAt
      updatedAt
    }
    ... on ApiError {
      code
      message
      description
    }
	}
}","variables":{"id":"5d25e85be063ebf062768e35"}}

{"query":"mutation($data: DoctorInput!) {
	createNewDoctor (data: $data) {
    __typename
    ... on Doctor {
      id
      socialCareNumber
      idCard
      name
      surname
      birthDate
      genre
      username
      role
      token
      lastLoginAt
      createdAt
      updatedAt
      enabled
      membershipNumber
      specialities
      assignedPatients
    }
    ... on ApiError {
      code
      message
      description
    }
	}
}","variables":{"data":{"socialCareNumber":"382900000001","idCard":"290000001A","name":"Philippa","surname":"Georgiou","birthDate":946721410000,"genre":"female","membershipNumber":"111111"}}}

{"query":"query ($id: String!) {
	doctor: getDoctorById (id: $id) {
    __typename
    ... on Doctor {
      id
      socialCareNumber
      idCard
      name
      surname
      birthDate
      genre
      username
      role
      token
      lastLoginAt
      createdAt
      updatedAt
      enabled
      membershipNumber
      specialities
      assignedPatients
    }
    ... on ApiError {
      code
      message
      description
    }
	}
}","variables":{"id":"5d29c666eb682e6a6e92d9ad"}}

{"query":"mutation($data: PatientInput!) {
	createNewPatient (data: $data) {
    __typename
    ... on Patient {
      id
      socialCareNumber
      idCard
      name
      surname
      genre
      birthDate
      username
      role
      token
      lastLoginAt
      createdAt
      updatedAt
      enabled
      deceased
      medicalHistory {
        socialCareNumber
        medicalReports {
          bindToReport
          department
          reportType
          reportDate
          signedBy
          verification {
            required
            requiredTo
            verifiedBy
            verifiedAt
            notes
          }
          content
          attachedTests
        }
      }
    }
    ... on ApiError {
      code
      message
      description
    }
	}
}","variables":{"data":{"socialCareNumber":"380000000002","idCard":"000000002A","name":"Salvador","surname":"Dal√≠","birthDate":946721410000,"genre":"male"}}}

{"query":"query {
  patients: getAllPatients {
    id
    socialCareNumber
    name
    surname
    birthDate
  }
}"}

{"query":"query ($id: String!) {
	patient: getPatientById (id: $id) {
    __typename
    ... on Patient {
      id
      socialCareNumber
      name
      surname
      birthDate
      medicalHistory {
        medicalReports {
          department
          reportType
          reportDate
        }
      }
    }
    ... on ApiError {
      code
      message
      description
    }
	}
}

