    ... on ApiError {
      code
      message
      description
    }
	}
}","variables":{"data":{"bindToReport":"5d2a5e74fa44c73040cc661c","department":"Psicology","reportType":"Appointment","signedBy":"5d29c666eb682e6a6e92d9ad","content":"Psicoterapist appointment 1."}}}

